/**
 * 二维码检测工具模块
 * 使用 jsQR 库检测图像中的二维码位置
 */
import jsQR from 'jsqr';

/**
 * 检测图像中的二维码位置
 * @param {ImageData} imageData - Canvas 的 ImageData 对象
 * @returns {Object|null} 检测结果，包含位置信息
 */
export function detectQRCode(imageData) {
  const {
    data,
    width,
    height
  } = imageData;

  // 使用 jsQR 检测二维码
  const result = jsQR(data, width, height, {
    inversionAttempts: 'dontInvert' // 优化性能，不尝试反色检测
  });
  if (!result) {
    return null;
  }

  // 提取定位点坐标
  const {
    location
  } = result;
  return {
    // 四个角点坐标
    topLeft: {
      x: location.topLeftCorner.x,
      y: location.topLeftCorner.y
    },
    topRight: {
      x: location.topRightCorner.x,
      y: location.topRightCorner.y
    },
    bottomRight: {
      x: location.bottomRightCorner.x,
      y: location.bottomRightCorner.y
    },
    bottomLeft: {
      x: location.bottomLeftCorner.x,
      y: location.bottomLeftCorner.y
    },
    // 边界框（用于快速定位）
    boundingBox: calculateBoundingBox(location),
    // 中心点
    center: calculateCenter(location)
  };
}

/**
 * 计算边界框
 * @param {Object} location - jsQR 返回的 location 对象
 * @returns {Object} 边界框 { x, y, width, height }
 */
function calculateBoundingBox(location) {
  const points = [location.topLeftCorner, location.topRightCorner, location.bottomRightCorner, location.bottomLeftCorner];
  const xs = points.map(p => p.x);
  const ys = points.map(p => p.y);
  const minX = Math.min(...xs);
  const maxX = Math.max(...xs);
  const minY = Math.min(...ys);
  const maxY = Math.max(...ys);
  return {
    x: minX,
    y: minY,
    width: maxX - minX,
    height: maxY - minY
  };
}

/**
 * 计算二维码中心点
 * @param {Object} location - jsQR 返回的 location 对象
 * @returns {Object} 中心点坐标 { x, y }
 */
function calculateCenter(location) {
  const {
    topLeftCorner,
    bottomRightCorner
  } = location;
  return {
    x: (topLeftCorner.x + bottomRightCorner.x) / 2,
    y: (topLeftCorner.y + bottomRightCorner.y) / 2
  };
}

/**
 * 在 Canvas 上绘制检测结果
 * @param {CanvasRenderingContext2D} ctx - Canvas 2D 上下文
 * @param {Object} detection - 检测结果
 * @param {Object} options - 绘制选项
 */
export function drawDetectionResult(ctx, detection, options = {}) {
  const {
    strokeColor = '#00f5a0',
    fillColor = 'rgba(0, 245, 160, 0.1)',
    lineWidth = 3,
    cornerRadius = 8,
    showCenter = true,
    showCorners = true
  } = options;
  const {
    topLeft,
    topRight,
    bottomRight,
    bottomLeft,
    center
  } = detection;
  ctx.save();

  // 绘制填充区域
  ctx.fillStyle = fillColor;
  ctx.beginPath();
  ctx.moveTo(topLeft.x, topLeft.y);
  ctx.lineTo(topRight.x, topRight.y);
  ctx.lineTo(bottomRight.x, bottomRight.y);
  ctx.lineTo(bottomLeft.x, bottomLeft.y);
  ctx.closePath();
  ctx.fill();

  // 绘制边框
  ctx.strokeStyle = strokeColor;
  ctx.lineWidth = lineWidth;
  ctx.lineCap = 'round';
  ctx.lineJoin = 'round';

  // 绘制四边形边框
  ctx.beginPath();
  ctx.moveTo(topLeft.x, topLeft.y);
  ctx.lineTo(topRight.x, topRight.y);
  ctx.lineTo(bottomRight.x, bottomRight.y);
  ctx.lineTo(bottomLeft.x, bottomLeft.y);
  ctx.closePath();
  ctx.stroke();

  // 绘制角点标记
  if (showCorners) {
    const corners = [topLeft, topRight, bottomRight, bottomLeft];
    ctx.fillStyle = strokeColor;
    corners.forEach(corner => {
      ctx.beginPath();
      ctx.arc(corner.x, corner.y, cornerRadius, 0, Math.PI * 2);
      ctx.fill();
    });
  }

  // 绘制中心点
  if (showCenter) {
    ctx.fillStyle = strokeColor;
    ctx.beginPath();
    ctx.arc(center.x, center.y, 6, 0, Math.PI * 2);
    ctx.fill();

    // 绘制十字线
    ctx.strokeStyle = strokeColor;
    ctx.lineWidth = 2;
    const crossSize = 15;
    ctx.beginPath();
    ctx.moveTo(center.x - crossSize, center.y);
    ctx.lineTo(center.x + crossSize, center.y);
    ctx.moveTo(center.x, center.y - crossSize);
    ctx.lineTo(center.x, center.y + crossSize);
    ctx.stroke();
  }
  ctx.restore();
}

/**
 * 从视频帧获取 ImageData
 * @param {HTMLVideoElement} video - 视频元素
 * @param {HTMLCanvasElement} canvas - Canvas 元素
 * @returns {ImageData|null} ImageData 对象
 */
export function getImageDataFromVideo(video, canvas) {
  if (!video || video.readyState !== video.HAVE_ENOUGH_DATA) {
    return null;
  }
  const ctx = canvas.getContext('2d');
  canvas.width = video.videoWidth;
  canvas.height = video.videoHeight;
  ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
  return ctx.getImageData(0, 0, canvas.width, canvas.height);
}

/**
 * 从图片文件获取 ImageData
 * @param {File} file - 图片文件
 * @returns {Promise<{imageData: ImageData, width: number, height: number}>}
 */
export function getImageDataFromFile(file) {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.onload = e => {
      const img = new Image();
      img.onload = () => {
        const canvas = document.createElement('canvas');
        canvas.width = img.width;
        canvas.height = img.height;
        const ctx = canvas.getContext('2d');
        ctx.drawImage(img, 0, 0);
        const imageData = ctx.getImageData(0, 0, img.width, img.height);
        resolve({
          imageData,
          width: img.width,
          height: img.height,
          imageSrc: e.target.result
        });
      };
      img.onerror = () => reject(new Error('图片加载失败'));
      img.src = e.target.result;
    };
    reader.onerror = () => reject(new Error('文件读取失败'));
    reader.readAsDataURL(file);
  });
}

